// export const PlayerDispatch = React.createContext(null);

function ListHead(){
  const count = countLikes(players);


  return (
      <header>
            <h2>
                <button>야구 선수 추가하기</button>
            </h2>
            <p>

              좋아하는 야구 선수 수: 
            </p>
      </header>

  );

}
function App() {

    const [inputs, setInputs] = useState({
      playername: '',
      team: '',
      position: '',
      like : false
    });
    const { playername, team, position, like } = inputs;

    const onChange = e => {
      const { name, value } = e.target;
      setInputs({
        ...inputs,
        [name]: value
      });
    };

    const [players, setPlayers] = useState( [
      {
        id: 1,
        playername: '이대호',
        team: '롯데 자이언츠',
        position: '지명타자',
        like : false
      },
      {
        id: 2,
        playername: '손아섭',
        team: '롯데 자이언츠',
        position: '우익수',
        like : false
      },
      {
        id: 3,
        playername: '김원중',
        team: '롯데 자이언츠',
        position: '마무리 투수',
        like : false
      }
    ]);

    const nextId = useRef(4);

    const onCreate = () => {
      const player = {
        id: nextId.current,
        playername,
        team,
        position,
        like
      };
      setPlayers([...players, player]);

      setInputs({
        playername: '',
        team: '',
        position: '',
        like: false
      });
      nextId.current += 1;
    };

    const onToggle = id => {
      setPlayers(
        players.map(player =>
          player.id === id ? { ...player, like: !player.like } : player
        )
      );
    };

    const count = countLikes(players);



  return (
    <div className="App">
      <ListHead></ListHead>
      <>
      <CreatePlayer
          playername={playername}
          team={team}
          position={position}
          like = {like}
          onChange={onChange}
          onCreate={onCreate}
        />
      <PlayerList players={players} onToggle={onToggle}/>
      <div>좋아요 : {count}</div>
      </>
    </div>
  );

}

export default App;
