import React, {Component, useState} from 'react';

class LikeButton extends Component {
    constructor(){
        super();
    
    this.state = {
        color_black: true,
        }
    }

    changeButton(){
        this.setState({color_black: !this.state.color_black})        
    };
    render(){
        let bg = this.state.color_black ? this.props.color : this.props.color2
          
        return (
            
                <button style= {{backgroundColor : bg}} onClick={this.changeButton.bind(this)}>좋아요</button>
            
        )
      
};}

export default LikeButton;
