import React, {Component} from 'react';



class LikeButton extends Component {
    constructor(props){
        super(props);
    
    this.state = {
        clicked: true,
        backgroundColor:'gary'
        }
    }

    changeButton(){
        this.setState({clicked: !this.state.clicked})        
    };
    render(){
        let bg = this.state.clicked ? "white" : "gray"
          
        return (
            
            <button style= {{backgroundColor : bg}} onClick={this.changeButton.bind(this)}>좋아요</button>
            
        )
      
};}

export default LikeButton;
